INSERT INTO `search_notice` (`id`, `firstname`, `lastname`, `home`, `description`, `age`, `picture`, `status`, `created_at`, `updated_at`, `latest_city`, `latest_date`, `user_id`) VALUES
(1,	'Julie',	'Dubois',	'Paris',	'Elle est petite avec des cheveux longs et bruns, portant des lunettes et des boucles d\'oreilles en forme de cœur. Elle a une cicatrice sur la joue gauche.',	30,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/julie-1.jpg',	0,	'2024-02-29 20:03:41',	NULL,	'Marseille',	'2024-06-05 06:34:16',	4),
(2,	'Jean',	'Dupont',	'Marseille',	'Grand et mince, avec des cheveux marron courts et des yeux marron foncé. Il porte une barbe bien entretenue et a une tatouage tribal sur son avant-bras gauche.',	40,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/jean-2.jpg',	1,	'2024-02-29 02:11:13',	NULL,	'Marseille',	'2024-02-29 02:11:13',	7),
(3,	'Sophie',	'Martin',	'Lyon',	'De taille moyenne avec des cheveux roux bouclés et des taches de rousseur sur le visage. Elle porte un piercing au nez et un tatouage d\'étoile sur le poignet droit.',	25,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/sophie-3.jpg',	1,	'2024-01-28 19:23:10',	NULL,	'Lyon',	'2024-01-28 19:23:10',	6),
(4,	'Thomas',	'Lefebvre',	'Toulouse',	'Costaud avec des cheveux noirs courts et des yeux marrons perçants. Il a une cicatrice sur le front et des boucles d\'oreilles en argent.',	35,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/thomad-4.jpg',	0,	'2024-02-29 01:23:44',	NULL,	'Marseille',	'2024-06-05 16:45:49',	4),
(5,	'Marie',	'Leroy',	'Nice',	'Grande et élancée avec des cheveux blonds platine coupés courts et des yeux bleus étincelants. Elle porte un collier en argent et une bague en forme de papillon.',	40,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/Marie-5.jpg',	0,	'2024-02-21 21:22:25',	NULL,	'Lyon',	'2024-06-05 20:13:59',	10),
(6,	'Pierre',	'Moreau',	'Bordeaux',	'De stature moyenne avec des cheveux gris poivre et sel et une moustache soigneusement taillée. Il porte des lunettes et une chemise à carreaux.',	50,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/Pierre-6.jpg',	1,	'2024-01-09 16:29:48',	NULL,	'Bordeaux',	'2024-01-09 16:29:48',	5),
(7,	'Caroline',	'Girard',	'Lille',	'Petite avec des cheveux châtains ondulés et des yeux marrons. Elle porte un collier en perles et a un tatouage de fleur sur la cheville gauche.',	30,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/caroline-7.jpg',	1,	'2024-01-01 18:26:31',	NULL,	'Toulouse',	'2024-01-18 12:04:41',	1),
(8,	'Nicolas',	'Andre',	'Strasbourg',	'De grande taille avec des cheveux bruns désordonnés et une barbe de quelques jours. Il porte un piercing à l\'oreille gauche et une montre en cuir.',	45,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/nicolas-8.jpg',	0,	'2024-03-28 21:38:27',	NULL,	'Nantes',	'2024-04-05 05:07:40',	9),
(9,	'Camille',	'Thomas',	'Nantes',	'Elle est de taille moyenne avec des cheveux noirs raides et des lunettes rondes. Elle porte un chemisier à motifs floraux et un bracelet en argent.',	35,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/camille-9.jpg',	1,	'2024-04-21 13:29:54',	NULL,	'Bordeaux',	'2024-01-14 16:14:12',	8),
(10,	'Vincent',	'Petit',	'Montpellier',	'Costaud avec des cheveux blonds en brosse et des yeux bleu clair. Il a une cicatrice sur le menton et porte un piercing à la lèvre inférieure.',	40,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/vincent-10.jpg',	0,	'2024-04-28 11:53:00',	NULL,	'Toulouse',	'2024-05-26 07:13:34',	9),
(22,	'Lee',	'Jennah',	'Toulouse',	'Cette personne mesure environ 1m75, a une corpulence moyenne, des cheveux noirs mi-longs et des yeux marron foncé. Elle a été vue pour la dernière fois portant un manteau noir et un jean bleu. Aucun signe distinctif visible, tel que cicatrices ou tatouages, n\'a été noté. ',	30,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/uifaces-human-image-1-6661646914f1a142979901.jpg',	0,	'2024-06-06 07:25:29',	'2024-06-06 07:25:29',	'Beauvais',	'2024-05-26 19:33:04',	19),
(23,	'Elisa',	'Dubois',	'Rennes',	'Nous recherchons activement une petite fille rousse répondant au nom d\'Elisa Dubois. Âgée de 8 ans, elle mesure environ 1m30. Elle a les cheveux roux, longs et bouclés, et des yeux verts. Elisa a été vue pour la dernière fois portant une robe bleue à motifs floraux et des chaussures blanches. Elle n\'a aucune cicatrice ou marque distinctive visible. \r\n\r\n\r\n\r\n\r\n\r\n\r\n',	13,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/uifaces-human-image-2-666164cdd0741978934618.jpg',	0,	'2024-06-06 07:27:09',	'2024-06-06 07:27:09',	NULL,	NULL,	19),
(24,	'Pierre',	'Martin',	'Nice',	'Nous recherchons activement M. Pierre Martin, un homme de 40 ans. Il mesure environ 1m80 et a une corpulence moyenne. Pierre a les cheveux bruns courts et les yeux marron. Il porte toujours un béret, peu importe la saison. La dernière fois qu\'il a été vu, il portait un manteau gris et un pantalon noir. Aucun signe distinctif, tel que cicatrices ou tatouages, n\'a été noté.\r\n\r\n\r\n\r\n',	40,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/uifaces-human-image-3-666165bf2e403195417314.jpg',	0,	'2024-06-06 07:31:11',	'2024-06-06 07:31:11',	'Nantes',	'2024-06-05 07:59:10',	19),
(25,	'Sophie',	'Durand',	'Nantes',	'Nous recherchons activement Mme Sophie Durand, une femme âgée de 35 ans. Elle mesure environ 1m65 et a une corpulence mince. Sophie a les cheveux courts, châtain foncé, et les yeux marron. Elle a été vue pour la dernière fois portant un blouson en cuir noir et un jean délavé. Aucun signe distinctif visible, tel que cicatrices ou tatouages, n\'a été noté. \r\n\r\n\r\n\r\n\r\n\r\n',	35,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/uifaces-human-image-4-6661666406aba911434517.jpg',	0,	'2024-06-06 07:33:56',	'2024-06-06 07:33:56',	NULL,	NULL,	19),
(26,	'Penda',	'Ibra',	'Grenoble',	'Elle mesure environ 1m70 et a une corpulence athlétique. Ba les cheveux long  et les yeux foncé. Elle a été vue pour la dernière fois portant une veste rouge et un pantalon beige. Aucun signe distinctif visible, tel que cicatrices ou tatouages, n\'a été noté.',	30,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/uifaces-human-image-5-666167acd2cf1686990217.jpg',	0,	'2024-06-06 07:39:24',	'2024-06-06 07:39:24',	NULL,	NULL,	19),
(27,	'Bernard',	'Hont',	'Brest',	'Vieil homme vivant dans son phare',	79,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/hontb-66616fb2a1493477972866.png',	1,	'2024-06-06 08:13:38',	'2024-06-06 08:13:38',	'Marseille',	'2024-06-06 08:13:49',	15),
(28,	'C10',	'PO',	'Tatooine',	'Beep beep boop! Bip bip bip! Beep-boop bip bip boop. Beep boop bip! Bip bip beep! Boop beep beep bip boop. Boop beep bip bip boop. Bip boop beep beep. Boop beep bip bip boop. Bip boop beep beep bip boop. Beep boop bip! Bip bip bip beep boop!',	98,	'https://apocalypse.florencecarlin-server.eddi.cloud/images/searchnotices/uifaces-robot-image-66617b6e7dbd1718171232.jpg',	0,	'2024-06-06 09:03:42',	'2024-06-06 09:03:42',	NULL,	NULL,	15);
